package odev_1;

import java.util.Scanner;

public class HesapMakinesi {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		Scanner input= new Scanner(System.in); 
		 int n1,n2;
		 System.out.println("Birinci sayiyi giriniz :");
		 n1=input.nextInt();
		 System.out.println("Ýkinci sayiyi giriniz :");
		 n2=input.nextInt();
		 
	    
	 
	 System.out.println("1-Toplama\n2-Çýkarma\n3-Çarpma\n4-Bölme");
	  System.out.println("seçiniz :");
	  
	  int secim;
	     secim= input.nextInt();
	  
	  switch (secim) {
	  
	  case 1:
		  System.out.println("Toplam sonucu:"+(n1+n2));
		  break;
	  case 2:
		  System.out.println("Çýkarma sonucu:"+(n1-n2));
		  break;
	  case 3 :
		  System.out.println("Çarpma sonucu:"+(n1*n2));
		  break;
		  
	  case 4 :
	  {
		 if(n2!=0) {
		 
		  System.out.println("Bölme sonucu :"+(n1/n2));
	
		 }else {
	   
			   System.out.println ("Bir sayi sýfýra bölünemez.!");
	
		 }
		 break; 
	  }
		  
	   
	  default:
		  
		 System.out.println ("hatalý seçim yaptýnýz.Lütfen yeniden deneyiniz!");
		 break;
	  }  
	  
	  }


	}


